import moment from 'moment'; // Example for onSort prop
import React, { Component } from 'react'; // Import React
import axios from 'axios';
import { render } from 'react-dom'; // Import render method
import Datatable from 'react-bs-datatable'; // Import this package

class DataTable extends Component{ 
  constructor(props){
    super(props);
    this.state = {
      contList : []
    }
    this.productFetch = this.productFetch.bind(this);

  }  

  productFetch(){
    axios.post('http://localhost:3200/api/fetchProduct',{
      // userId: this.props.userId,
      // email: this.props.email,
    }).then((resp)=>{
      console.log('##################',resp.data.user);
      this.setState({
       contList: resp.data.user,
      })
    })
  }

  componentWillMount (){
    this.productFetch();
  }

render(){
  console.log('XXXXXXXXXXXXXXXXXXXXXXXX',this.state.contList);
const header = [
  { title: 'Username', prop: 'username', sortable: true, filterable: true },
  { title: 'Name', prop: 'realname', sortable: true },
  // { title: 'Name Uppercased', prop: 'realnameuppercase', cell: (row) => row.realname.toUpperCase() },
  { title: 'Location', prop: 'location' },
  { title: 'Last Updated', prop: 'date', sortable: true },

];

let state = this.state;
const body = [
  state.contList.map((e,i)=>{
      header.username = e.category
  })
]
console.log(state.contList[0]);

const onSortFunction = {
  date(columnValue) {
    // Convert the string date format to UTC timestamp
    // So the table could sort it by number instead of by string
    return moment(columnValue, 'Do MMMM YYYY').valueOf();
  },
};

const customLabels = {
  first: '<<',
  last: '>>',
  prev: '<',
  next: '>',
  show: 'Display',
  entries: 'rows',
  noResults: 'There is no data to be displayed',
};

// In your render method
return(

<Datatable
  tableHeader={header}
  tableBody={body}

  keyName="userTable"
  tableClass="striped hover responsive"
  rowsPerPage={5}
  rowsPerPageOption={[5, 10, 15, 20]}
  initialSort={{prop: "username", isAscending: true}}
  onSort={onSortFunction}
  labels={customLabels}
/>
)
}
}

export default DataTable;
