import React from 'react';
import ReactDOM from 'react-dom';
import axios from 'axios';
import {withRouter} from 'react-router-dom';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import './productlistpage.css';
import swal from 'sweetalert';
import action from '../action/action';
class Productlistpage extends React.Component{
   constructor(props){
     super(props);
     this.state = {
       contList : []
     }
     this.productFetch = this.productFetch.bind(this);
   }  

   productFetch(){
     axios.post('http://localhost:3200/api/fetchProduct',{
       userId: this.props.userId,
       email: this.props.email,
     }).then((resp)=>{
       console.log('##################',resp.data.user);
       this.setState({
        contList: resp.data.user,
       })
     })
   }

   componentWillMount (){
     this.productFetch();
   }

   product(e,i){
    console.log('WWWWWWWWWWWWWWWWWWWWWWWWWWWW',e.state.contList[i]._id);
    axios.post('http://localhost:3200/api/fetchProductList',{
      productId :e.state.contList[i]._id
    }).then((response)=>{
     if(response){
       console.log('%%%%%%%%%%%%%%%%%',payload);
       this.props.product({
         type: 'product',
         payload: response.data
        })
     }
      console.log('---------------------------',response);
    })
    // this.props.history.push('/Productdetail');
   }



   deleteProduct(e){
    swal({
      title: "Are you sure?",
      text: "Once deleted, you will not be able to recover this imaginary file!",
      icon: "warning",
      buttons: true,
      dangerMode: true,
    }).then((willDelete)=>{
      if(willDelete){
        axios.post('http://localhost:3200/api/deleteProduct',{
          productId: e,
        }).then(res=>{
          swal("Poof! Your imaginary file has been deleted!",{
            icon:"success",
          });
        })
        setTimeout(function(){
          return window.location = '/Productlist';
        },2000)
      }else{
        swal("Your imaginary file is safe!");
      }
    });
   }


	render(){
    // console.log('qqqqqqqqq',this.props.productId);
		return(
        <div className="my-3 my-md-5">
          <div className="container">
            <div className="page-header">
              <h4 className="page-title">Product List</h4>
              <ol className="breadcrumb">
                <li className="breadcrumb-item"><a href="#">Home</a></li>
                <li className="breadcrumb-item active" aria-current="page">Product List</li>
              </ol>
            </div>
            <div className="row">
              <div className="col-md-12 col-lg-12">
              <div className="card">
                <div className="card-body">
                                  <div className="table-responsive">
                  <table id="example" className="table table-striped table-bordered">
                    <thead>
                      <tr>
                        <th className="wd-15p">Product Id</th>
                        <th className="wd-15p">Category</th>
                        <th className="wd-15p">Product Name</th>
                        <th className="wd-20p">Price</th>
                        <th className="wd-20p">Quantity</th>
                        <th className="wd-25p">Status</th>
                        <th className="wd-15p">Action</th>
                      </tr>
                    </thead>
                    <tbody>
                      {
                        this.state.contList.map((e,i)=>{
                          return(
                            <React.Fragment key = {i}>
                                  <tr>
                              <td>#P1001</td>
                              <td>{e.category}</td>
                              <td>{e.productName}</td>
                              <td>{e.productPrice}</td>
                              <td>{e.quantity}</td>
                        <td>
                        <div className="card-options">
                            <label className="custom-switch m-0">
                              <input type="checkbox" value="1" className="custom-switch-input" />
                              <span className="custom-switch-indicator"></span>
                            </label>
                          </div>
                        </td>
                        <td>
                          <div className="actiontrans">
                            <a href="#" onClick = {(e)=> this.product(this, i)} value = {e._id}><i className="fa fa-eye"></i> </a>
                            <a href="#" ><i className="fa fa-edit"></i>  </a>
                            <a href="#"onClick = {this.deleteProduct.bind(this, e._id)} ><i className="fa fa-trash"></i>  </a>
                          </div>
                        </td>
                      </tr>
                             </React.Fragment>
                          )
                        })
                      }
                    </tbody>
                  </table>
                </div>
                                </div>
              </div>
              </div>
            </div>
          </div>
        </div>


			)
	}
}

function mapStateToProps(state){
  // console.log('pppppppppppppppppppppp',state.productReduce.productId);
  return {
    // productState : state.productReduce.productState,
    // productId : state.productReduce.productId,
    authenticateState : state.inititateState.authenticateState,
    email: state.inititateState.email,
    userId: state.inititateState.userId,
  }
}

function mapDispatchToProps(dispatch) {
  return{
    product : bindActionCreators(action.product , dispatch)
  }
}

export default withRouter(connect(mapStateToProps, mapDispatchToProps)(Productlistpage));
