import React from 'react';
import ReactDOM from 'react-dom';
import {withRouter} from 'react-router-dom';
import {connect} from 'react-redux';
import {bindActionCreators} from 'redux';
import validator from 'validator';
import axios from 'axios';
import swal from 'sweetalert';
import $ from 'jquery';
import './createprofilepage.css';
import { access } from 'fs';
import action from '../action/action';
class Createprofilepage extends React.Component{
  constructor(props){
    super(props);
    this.state = {
      name           : {value : this.props.name , isValidate: true, message: ''},
      mobile         : {value: '', isValidate: true, message: '' },
      storeName      : {value: '', isValidate: true, message: ''},
      storeEmail     : {value: '', isValidate: true, message: ''},
      storeMobile    : {value: '', isValidate: true, message: ''},
      streetName     : {value: '', isValidate: true, message: ''},
      location       : {value:'',  isValidate: true, message:''},
      zipCode        : {value: '', isValidate: true, message: ''},
      city           : {value: '', isValidate: true, message: ''},
    }
    this.handleChage = this.handleChage.bind(this);
    this.finish = this.finish.bind(this);
  }

  handleChage(event){
    const{name,value} = event.target;
    let state = this.state;
    state[name].message = ''
    state[name].value = value;
    this.setState(state);
  }

  finish(event){
    event.preventDefault();
    // let isValid = this.validate();
    var that = this;
    let obj = {};
